function WKDetailsSetDefaultConfig()
    if not _detalhes_global then _detalhes_global = {} end
    _detalhes_global = WKDetailsConfig
end

function WKDetailsIsAvailable()
    return _detalhes and _detalhes_global
end

function WKDetailsForceReloadConfig()
    -- Force Details to reload the configuration
    if _detalhes then
        if _detalhes.RestoreProfileCookie then
            _detalhes:RestoreProfileCookie()
        end
        if _detalhes.RefreshAnimationFunctions then
            _detalhes:RefreshAnimationFunctions()
        end
        if _detalhes.RefreshMainWindow then
            _detalhes:RefreshMainWindow(-1, true)  -- 添加true参数强制刷新
        end
        if WKDEBUG then
            _detalhes:Msg("配置已重新加载")
        end
    end
end